  id INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
  title VARCHAR(300) NOT NULL,
  author VARCHAR(100) not null,
  body VARCHAR(5000) not null,
  category VARCHAR(100) not null,
  img VARCHAR(1000) not null
);

